
/*! minimal sweetalert2-like wrapper for offline use */
window.Swal = {
  fire: function(opts) {
    return new Promise((res) => {
      // create overlay modal similar to sweetalert2
      const overlay = document.createElement('div');
      overlay.style.position='fixed'; overlay.style.left=0; overlay.style.top=0; overlay.style.right=0; overlay.style.bottom=0;
      overlay.style.background='rgba(0,0,0,0.4)'; overlay.style.display='flex'; overlay.style.alignItems='center'; overlay.style.justifyContent='center'; overlay.style.zIndex=10000;
      const box = document.createElement('div');
      box.style.background='#fff'; box.style.padding='18px'; box.style.borderRadius='10px'; box.style.width='420px'; box.style.boxShadow='0 18px 60px rgba(0,0,0,0.2)';
      const title = document.createElement('h3'); title.textContent = opts.title || ''; title.style.margin='0 0 8px 0';
      const text = document.createElement('div'); text.innerHTML = opts.html || opts.text || ''; text.style.marginBottom='12px';
      box.appendChild(title); box.appendChild(text);
      const actions = document.createElement('div'); actions.style.textAlign='right';
      const cancelBtn = document.createElement('button'); cancelBtn.textContent = opts.cancelButtonText || 'Cancel'; cancelBtn.className='btn-ghost'; cancelBtn.style.marginRight='8px';
      const okBtn = document.createElement('button'); okBtn.textContent = opts.confirmButtonText || 'OK'; okBtn.className='btn-primary';
      if (opts.showCancelButton) actions.appendChild(cancelBtn);
      actions.appendChild(okBtn);
      box.appendChild(actions); overlay.appendChild(box); document.body.appendChild(overlay);
      okBtn.addEventListener('click', ()=>{ document.body.removeChild(overlay); res({isConfirmed:true}); });
      cancelBtn.addEventListener('click', ()=>{ document.body.removeChild(overlay); res({isConfirmed:false}); });
    });
  }
};
