
/*! minimal Toastify-like wrapper for offline use */
window.Toastify = function(opts) {
  return {
    showToast: function() {
      const el = document.createElement('div');
      el.textContent = opts.text || '';
      el.style.position='fixed'; el.style.right='20px'; el.style.bottom='20px'; el.style.zIndex=10000;
      el.style.background = opts.backgroundColor || '#0b8a4a'; el.style.color = '#fff'; el.style.padding='10px 14px'; el.style.borderRadius='6px'; el.style.boxShadow='0 6px 18px rgba(0,0,0,0.12)';
      document.body.appendChild(el);
      setTimeout(()=> el.remove(), opts.duration || 3000);
    }
  };
};
